// N8N Code Node to remove all newline characters from previous node outputs

// Process all items in the array
// This will work regardless of whether we're running once for all items or once per item
for (let i = 0; i < items.length; i++) {
  const currentItem = items[i];

  // Check if the item has an output property
  if (currentItem.json && currentItem.json.output !== undefined) {
    // Replace all newline characters with empty string
    // Using a global regex to catch ALL instances of \n
    currentItem.json.output = currentItem.json.output.replace(/\\n/g, '');

    // Also replace actual newline characters if they exist
    currentItem.json.output = currentItem.json.output.replace(/\n/g, '');
  }
}

// Return the processed items array
return items;

// Fixed code to extract video URLs from Fal.ai response structure
// This handles the actual structure returned by the Fal.ai API

// Function to extract video info from the actual Fal.ai response structure
function extractVideoInfo(obj, index) {
  // Handle the actual Fal.ai structure: { video: { url: "...", ... } }
  if (obj && obj.video && obj.video.url) {
    return {
      index: index,
      videoUrl: obj.video.url,
      contentType: obj.video.content_type || 'video/mp4',
      fileName: obj.video.file_name || 'output.mp4',
      fileSize: obj.video.file_size || 0
    };
  }

  // Fallback: Handle the old expected structure (if API changes back)
  if (obj && obj.code === 200 && 
      obj.data && obj.data.output && obj.data.output.video_url) {
    return {
      index: index,
      taskId: obj.data.task_id,
      videoUrl: obj.data.output.video_url,
      prompt: obj.data.input.prompt
    };
  }

  // Handle direct URL structure
  if (obj && typeof obj === 'string' && obj.startsWith('http')) {
    return {
      index: index,
      videoUrl: obj
    };
  }

  return null;
}

// Initialize results array
const videoUrls = [];
let currentIndex = 1;

// n8n specific: loop through all items
for (const item of items) {
  console.log(`Processing item ${currentIndex}:`, JSON.stringify(item.json, null, 2));

  // Case 1: item.json is our array of video objects
  if (Array.isArray(item.json)) {
    item.json.forEach(entry => {
      const videoInfo = extractVideoInfo(entry, currentIndex);
      if (videoInfo) {
        videoUrls.push(videoInfo);
        currentIndex++;
      }
    });
  } 
  // Case 2: item.json is a single video object
  else if (typeof item.json === 'object') {
    const videoInfo = extractVideoInfo(item.json, currentIndex);
    if (videoInfo) {
      videoUrls.push(videoInfo);
      currentIndex++;
    }
  }
}

console.log(`Extracted ${videoUrls.length} video URLs:`, videoUrls);

// Return the extracted URLs as the output
return [{
  json: {
    totalVideos: videoUrls.length,
    videos: videoUrls
  }
}];
